import { HubConnectionBuilder, LogLevel } from '@aspnet/signalr'

const connection = new HubConnectionBuilder()
                        .withUrl('http://localhost:8000/chat-hub')
                        .withAutomaticReconnect()
                        .configureLogging(LogLevel.Information)
                        .build();

function onConnectionAsync() {
    connection.start();
}

function onDisconnectionAsync(){

}

function ReceiveMessage(){
    var message = {};
    connection.on("ReceiveMessage", (obj) => {
        var message = JSON.parse(obj);

    });
}

function SendMessage(message){
    connection.invoke("SendGroup", message).catch(function (err) {
        return console.error(err.toString());
    });
}

export default {
    onConnectionAsync,
    onDisconnectionAsync,
    ReceiveMessage,
    SendMessage,
}
