import axios from 'axios';
import { createStore } from 'vuex'
import constants from '../constants/index.js';


const store = createStore({
    state: () => ({ 
        user_id: '',
        access_token: '',
    }),
    getters: {
        getAccessToken: (state) => state.access_token,
        getUserId: (state) => state.user_id
    },
    mutations: {
        setAccessToken: (state, token) => state.access_token = token,
        setUserId: (state, userId) => state.user_id = userId,
        getSearchConnection: async (state, name) => {
            try {
                var res = (await axios.get(constants.BASE_URL + '/api/user/search?search_value=' + name), {
                    headers: {
                        "Access-Control-Allow-Headers" : "*",
                        "Authorization": "Bearer " + state.access_token,
                    }
                }).data;
                console.log(res);
                return res;
            }
            catch (err) {
                console.log(err);    
            }
        }
    },
    actions: {
    }
});

export default store;
